			m_container = container;
			m_items = new ArrayList ();
		}

		public void Add (FieldDefinition value)
		{
			if (OnFieldDefinitionAdded != null && !this.Contains (value))
				OnFieldDefinitionAdded (this, new FieldDefinitionEventArgs (value));
			m_items.Add (value);
		}

